import React, { useEffect, useState } from "react";
import { w3cwebsocket as W3CWebSocket } from "websocket";
import { useNavigate } from "react-router-dom";
import LoadingProduct from "../../assets/icons/loadingProduct.svg";
import { useMessage } from '../../Components/MessageContext';

const WebSocketComponent = () => {
  const [isConnecting, setIsConnecting] = useState(true);
  const navigate = useNavigate();
  const {
    socket,
    setSocket,
    USBList,
    setUSBList,
    wifiSSID,
    setwifiSSID,
    wifiPassword,
    setwifiPassword,
    fourG_apn,
    setfourG_apn,
    fourG_user,
    setfourG_user,
    fourG_password,
    setfourG_password,
    fourG_pin,
    setfourG_pin,
    items,
    setItems,
    containerRef,
  } = useMessage();

  const handleAddItem = (message: string) => {
    setItems([...items, message]);
    if (containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  };


  const Tick = () => {
    return <img className="tick" src="/assets/img/tik.png" alt="" />
  };

  const connectWebSocket = () => {
    console.log("Web", isConnecting)
    // if (isConnecting) return;

    setIsConnecting(true);


    const newSocket = new W3CWebSocket('ws://' + window.location.hostname + ':4000');

    newSocket.onopen = () => {
      console.log("WebSocket bağlantısı kuruldu.");
      navigate("");
      setSocket(newSocket);
      setIsConnecting(false);
    };

    newSocket.onmessage = (message) => {
      const jsonData = JSON.parse(message.data.toString());
      console.log(jsonData);
      switch (jsonData.Command) {
        case "USBList":
          setUSBList(jsonData.Data)
          break
        case "Config":
          if (jsonData.Data) {
            setwifiSSID(jsonData.Data.wifiSSID)
            setwifiPassword(jsonData.Data.wifiPassword)
            setfourG_apn(jsonData.Data.fourG_apn)
            setfourG_user(jsonData.Data.fourG_user)
            setfourG_password(jsonData.Data.fourG_password)
            setfourG_pin(jsonData.Data.fourG_pin)
          }
          break


        // case "NetworkPriority":
        //   setNetworkPriority(message.data.toString());
        //   break

      }

    };

    newSocket.onerror = (error) => {
      console.error("WebSocket hatası:", error);
    };

    newSocket.onclose = () => {
      console.log("WebSocket bağlantısı kapatıldı.");
      setSocket(null);
      setIsConnecting(false);

      setTimeout(() => {
        connectWebSocket();
      }, 1000);
    };
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log("connectWebSocket...")
        connectWebSocket();
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();

    return () => {
      if (socket) {
        console.log("WebSocket bağlantısı kapatıldı.");
        socket.close();
      }
    };
  }, []);

  return <div></div>;
};

export default WebSocketComponent;
