import React, { useEffect, useState } from "react";
import { w3cwebsocket as W3CWebSocket } from "websocket";
import { useNavigate } from "react-router-dom";
import LoadingProduct from "../../assets/icons/loadingProduct.svg";
import {useMessage} from '../../Components/MessageContext';

const WebSocketComponent = () => {
  const [isConnecting, setIsConnecting] = useState(true);
  const { socket, setSocket } = useMessage();
  

  const navigate = useNavigate();

  const connectWebSocket = () => {
    console.log("Web", isConnecting)
    // if (isConnecting) return;

    setIsConnecting(true);


    const newSocket = new W3CWebSocket('ws://' + window.location.hostname + ':4000');

    newSocket.onopen = () => {
      console.log("WebSocket bağlantısı kuruldu.");
      navigate("");
      setSocket(newSocket);
      setIsConnecting(false);
    };

    newSocket.onmessage = (message) => {
      const jsonData = JSON.parse(message.data.toString());
      console.log(jsonData);
      switch (jsonData.Command) {
        case "USBList":
          localStorage.setItem('USBList', jsonData);
          break

        // case "NetworkPriority":
        //   setNetworkPriority(message.data.toString());
        //   break
        
        }
    
    };

    newSocket.onerror = (error) => {
      console.error("WebSocket hatası:", error);
    };

    newSocket.onclose = () => {
      console.log("WebSocket bağlantısı kapatıldı.");
      setSocket(null);
      setIsConnecting(false);

      setTimeout(() => {
        connectWebSocket();
      }, 1000);
    };
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log("connectWebSocket...")
        connectWebSocket();
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();

    return () => {
      if (socket) {
        console.log("WebSocket bağlantısı kapatıldı.");
        socket.close();
      }
    };
  }, []);

  return <div></div>;
};

export default WebSocketComponent;
