import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from "react-i18next";
import "./home.css";
import { toast } from "react-toastify";
import { useMessage } from '../../Components/MessageContext';

const Home = () => {
    const navigate = useNavigate();
    const { socket, setSocket } = useMessage();

    const [selectedUSB, setSelectedUSB] = useState('');
    const [selectedETH, setSelectedETH] = useState('');
    const [wifiSSID, setwifiSSID] = useState('');
    const [wifiPassword, setwifiPassword] = useState('');
    const [modelId, setmodelId] = useState('');
    const [chargePointId, setchargePointId] = useState('');

    const { USBList, setUSBList } = useMessage();
    const { ETHList, setETHList } = useMessage();

    // useEffect(() => {
    //     console.log("hey")
    // }, [wifiSSID]);

    function verifyInputs() {
        if (selectedUSB == "") {
            toast.error("Lütfen USB seçiniz!")
            return false
        }
        else if (selectedETH == "") {
            toast.error("Lütfen cihazın ethernet adresini seçiniz!")
            return false
        }
        else if (wifiSSID == "") {
            toast.error("Lütfen cihazın bağlanacağı wifi adresini giriniz!")
            return false
        }
        else if (wifiPassword == "") {
            toast.error("Lütfen cihazın bağlanacağı wifi şifresini giriniz!")
            return false
        }
        else if (modelId == "") {
            toast.error("Lütfen cihazın model Id barkodunu okunuz!")
            return false
        }
        else if (chargePointId == "") {
            toast.error("Lütfen cihazın Charge Point Id barkodunu okunuz!")
            return false
        }
    }

    function testStart() {
        if (verifyInputs()) {
            if (socket) {
                if (socket.readyState === socket.OPEN) {
                    socket.send(JSON.stringify({
                        "Command": "EthernetSettings",
                        "Data": {

                        }
                    }));

                } else {
                    toast.error("");
                }

            }

        }
    }

        function fakeTestStart() {
            if (verifyInputs()) {

            }
        }

        const handleUSBChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
            setSelectedUSB(event.target.value);
        };

        const handleETHChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
            setSelectedETH(event.target.value);
        };

        const handlewifiSSIDChange = (event: React.ChangeEvent<HTMLInputElement>) => {
            setwifiSSID(event.target.value);
        };

        const handlewifiPasswordChange = (event: React.ChangeEvent<HTMLInputElement>) => {
            setwifiPassword(event.target.value);
        };

        const handlemodelIdChange = (event: React.ChangeEvent<HTMLInputElement>) => {
            setmodelId(event.target.value);
        };

        const handlechargePointIdChange = (event: React.ChangeEvent<HTMLInputElement>) => {
            setchargePointId(event.target.value);
        };

        const Tick = () => {
            return <img className="tick" src="/assets/img/tik.png" alt="" />
        };


        return (
            <div className='fullscreen-div'>
                <div className='header'>Hera Test Uygulaması</div>
                <div className='text1'>Lütfen bigisyara USB ve Ethernet kablosunu takınız...</div>
                <div>
                    <span className='text2'>Bağlı USB Kablosu:</span>
                    <select className="text2" value={selectedUSB} onChange={handleUSBChange}>
                        <option value="" disabled>
                            USB Seçiniz
                        </option>
                        {USBList.map((usb, index) => (
                            <option key={index} value={usb}>
                                {usb}
                            </option>
                        ))}
                    </select>
                </div>
                <div>
                    <span className='text2'>Bağlı Ethernet IP'si:</span>
                    <select className="text2" value={selectedUSB} onChange={handleETHChange}>
                        <option value="" disabled>
                            Cihazın IP Adresini Seçiniz
                        </option>
                        {ETHList.map((usb, index) => (
                            <option key={index} value={usb}>
                                {usb}
                            </option>
                        ))}
                    </select>
                </div>
                <div className='text1'>Cihazın bağlanacağı wifi adresi ve şifresini giriniz...</div>
                <div>
                    <span className='text2'>Wifi SSID:</span>
                    <span className='text2'><input className="input" type="text" value={wifiSSID} onChange={handlewifiSSIDChange} /></span>
                </div>
                <div>
                    <span className='text2'>Wifi Password:</span>
                    <span className='text2'><input className="input" type="text" value={wifiPassword} onChange={handlewifiPasswordChange} /></span>
                </div>
                <div>
                    <span className='text2'>Model Id:</span>
                    <span className='text2'><input className="input" type="text" value={modelId} onChange={handlemodelIdChange} /></span>
                </div>
                <div>
                    <span className='text2'>Charge Point Id:</span>
                    <span className='text2'><input className="input" type="text" value={chargePointId} onChange={handlechargePointIdChange} /></span>
                </div>
                <button className='start' onClick={testStart}>Teste Başla</button>
                <button className='fakestart' onClick={fakeTestStart}>Fake Teste Başla</button>

                {/* <div className='text-header'>SMART FONKSİYON VE BAĞLANTI TESTLERİ </div>

            <div className='text3'>MCU kartına yazılım yükleniyor... <Tick /></div>

            <div className='text3'>Cihaz wifi ağına bağlanıyor... <Tick /></div>

            <div className='text3'>Bluetooth bağlantısı test ediliyor... <Tick /></div>

            <div className='text3'>4G test ediliyor... <Tick /></div>

            <div className='text3'>RS485 Hattı test ediliyor... <Tick /></div>

            <div className='text4'>Master kartı okutunuz. <Tick /> </div>

            <div className='text4'>Kullanıcı kartını okutunuz. <Tick /> </div>

            <div className='text4'>Diğer kullanıcı kartını okutunuz. <Tick /> </div> */}


                <div className='gap'></div>



            </div>
        );
    };

    export default Home;
