import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from "react-i18next";
import "./home.css";
import { toast } from "react-toastify";
import { useMessage } from '../../Components/MessageContext';

const Home = () => {
    const navigate = useNavigate();
    const [selectedUSB, setSelectedUSB] = useState('');
    const [selectedETH, setSelectedETH] = useState('');

    const { USBList, setUSBList } = useMessage();
    const { ETHList, setETHList} = useMessage();

    // useEffect(() => {
    //     if (USBList) {
    //         console.log("USBList", USBList)
    //     }
    // }, [USBList]);

    const handleUSBChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
        setSelectedUSB(event.target.value);
    };

    const handleETHChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
        setSelectedETH(event.target.value);
    };

    const Tick = () => {
        return <img className="tick" src="/assets/img/tik.png" alt="" />
    };


    return (
        <div className='fullscreen-div'>
            <div className='header'>Hera Test Uygulaması</div>
            <div className='text1'>Lütfen bigisyara USB ve Ethernet kablosunu takınız...</div>
            <div>
                <span className='text2'>Bağlı USB Kablosu:</span>
                <select className="text2" value={selectedUSB} onChange={handleUSBChange}>
                    <option value="" disabled>
                        USB Seçiniz
                    </option>
                    {USBList.map((usb, index) => (
                        <option key={index} value={usb}>
                            {usb}
                        </option>
                    ))}
                </select>
            </div>
            <div>
                <span className='text2'>Bağlı Ethernet IP'si:</span>
                <select className="text2" value={selectedUSB} onChange={handleETHChange}>
                    <option value="" disabled>
                        Cihazın IP Adresini Seçiniz
                    </option>
                    {ETHList.map((usb, index) => (
                        <option key={index} value={usb}>
                            {usb}
                        </option>
                    ))}
                </select>
            </div>
            <div className='text1'>Cihazın bağlanacağı wifi adresi ve şifresini giriniz...</div>
            <div>
                <span className='text2'>Wifi SSID:</span>
                <span className='text2'><input className="input" type="text" /></span>
            </div>
            <div>
                <span className='text2'>Wifi Password:</span>
                <span className='text2'><input className="input" type="text" /></span>
            </div>
            <div>
                <span className='text2'>Model Id:</span>
                <span className='text2'><input className="input" type="text" /></span>
            </div>
            <div>
                <span className='text2'>Charge Point Id:</span>
                <span className='text2'><input className="input" type="text" /></span>
            </div>
            <button className='start'>Teste Başla</button>

            {/* <div className='text-header'>SMART FONKSİYON VE BAĞLANTI TESTLERİ </div>

            <div className='text3'>MCU kartına yazılım yükleniyor... <Tick /></div>

            <div className='text3'>Cihaz wifi ağına bağlanıyor... <Tick /></div>

            <div className='text3'>Bluetooth bağlantısı test ediliyor... <Tick /></div>

            <div className='text3'>4G test ediliyor... <Tick /></div>

            <div className='text3'>RS485 Hattı test ediliyor... <Tick /></div>

            <div className='text4'>Master kartı okutunuz. <Tick /> </div>

            <div className='text4'>Kullanıcı kartını okutunuz. <Tick /> </div>

            <div className='text4'>Diğer kullanıcı kartını okutunuz. <Tick /> </div> */}


            <div className='gap'></div>



        </div>
    );
};

export default Home;
