import React, { createContext, useContext, useState, ReactNode } from 'react';
import { w3cwebsocket as W3CWebSocket } from "websocket";


interface MessageContextType {
  socket: W3CWebSocket | null;
  setSocket: (socket: W3CWebSocket | null) => void;

  USBList: string[];
  setUSBList: (USBList: string[]) => void;

  ETHList: string[];
  setETHList: (USBList: string[]) => void;


  networkjsonString: string | null;
  setNetworkPriority:(networkjsonString: string | null) => void;

  fourGSettingsString: string | null;
  setfourGSettingsString:(fourGSettingsString: string | null) => void;

  ethernetSettingsString: string | null;
  setethernetSettingsString:(ethernetSettingsString: string | null) => void;

  DNSSettingsString: string | null;
  setDNSSettingsString:(DNSSettingsString: string | null) => void;

  WifiSettingsString: string | null;
  setWifiSettingsString:(WifiSettingsString: string | null) => void;

  OcppSettingsString: string | null;
  setOcppSettingsString:(OcppSettingsString: string | null) => void;

  FunctionsSettingsString: string | null;
  setFunctionsSettingsString:(FunctionsSettingsString: string | null) => void;

  TimeZoneSettingsString: string | null;
  setTimeZoneSettingsString:(TimeZoneSettingsString: string | null) => void;

  BluetoothSettingsString: string | null;
  setBluetoothSettings:(BluetoothSettingsString: string | null) => void;

  GetVersionString: string | null;
  setGetVersionString:(GetVersionString: string | null) => void;

  ChargingString: string | null;
  setChargingString:(ChargingString: string | null) => void;

  DeviceStatusString: string | null;
  setDeviceStatusString:(DeviceStatusString: string | null) => void;

  ACCurrentString: string | null;
  setACCurrentString:(ACCurrentString: string | null) => void;

  MidMeterString: string | null;
  setMidMeterString:(MidMeterString: string | null) => void;

  
  menuCollapsed: boolean ;
  setMenuCollapsed:(menuCollapsed: boolean ) => void;
  
}

const MessageContext = createContext<MessageContextType | undefined>(undefined);

interface MessageProviderProps {
  children: React.ReactNode;
}

export const MessageProvider: React.FC<MessageProviderProps> = ({ children }) => {
  const [socket, setSocket] = useState<W3CWebSocket | null>(null);
  const [USBList, setUSBList] = useState<string[]>([]);
  const [ETHList, setETHList] = useState<string[]>([]);


  const [networkjsonString, setNetworkPriority] = useState<string | null>(null);
  const [fourGSettingsString, setfourGSettingsString] = useState<string | null>(null);
  const [ethernetSettingsString, setethernetSettingsString] = useState<string | null>(null);
  const [DNSSettingsString, setDNSSettingsString] = useState<string | null>(null);
  const [WifiSettingsString, setWifiSettingsString] = useState<string | null>(null);
  const [OcppSettingsString, setOcppSettingsString] = useState<string | null>(null);
  const [FunctionsSettingsString, setFunctionsSettingsString] = useState<string | null>(null);
  const [TimeZoneSettingsString, setTimeZoneSettingsString] = useState<string | null>(null);
  const [BluetoothSettingsString, setBluetoothSettings] = useState<string | null>(null);
  const [GetVersionString, setGetVersionString] = useState<string | null>(null);
  const [ChargingString, setChargingString] = useState<string | null>(null);
  const [DeviceStatusString, setDeviceStatusString] = useState<string | null>(null);
  const [ACCurrentString, setACCurrentString] = useState<string | null>(null);
  const [MidMeterString, setMidMeterString] = useState<string | null>(null);
  const [menuCollapsed, setMenuCollapsed] = useState(false);

  return (
    <MessageContext.Provider value={{socket, setSocket, 
                                    USBList, setUSBList,
                                    ETHList, setETHList,


                                    networkjsonString, setNetworkPriority, 
                                    fourGSettingsString, setfourGSettingsString,
                                    ethernetSettingsString, setethernetSettingsString,
                                    DNSSettingsString, setDNSSettingsString,
                                    WifiSettingsString, setWifiSettingsString,
                                    OcppSettingsString, setOcppSettingsString,
                                    FunctionsSettingsString, setFunctionsSettingsString,
                                    TimeZoneSettingsString, setTimeZoneSettingsString,
                                    BluetoothSettingsString, setBluetoothSettings,
                                    GetVersionString, setGetVersionString,
                                    ChargingString, setChargingString,
                                    DeviceStatusString, setDeviceStatusString,
                                    ACCurrentString, setACCurrentString,
                                    MidMeterString, setMidMeterString,
                                    menuCollapsed, setMenuCollapsed
    }}>
      {children}
    </MessageContext.Provider>
  );
};

export const useMessage = () => {
  const context = useContext(MessageContext);
  if (context === undefined) {
    throw new Error('useMessage must be used within a MessageProvider');
  }
  return context;
};
