import React, { useEffect, useState } from "react";
import { w3cwebsocket as W3CWebSocket } from "websocket";
import { useNavigate } from "react-router-dom";
import LoadingProduct from "../../assets/icons/loadingProduct.svg";
import { useMessage } from '../../Components/MessageContext';

const WebSocketComponent = () => {
  const [isConnecting, setIsConnecting] = useState(true);
  const { socket, setSocket } = useMessage();
  const { divs, setDivs } = useMessage();
  const { USBList, setUSBList } = useMessage();
  const navigate = useNavigate();

  const { wifiSSID, setwifiSSID } = useMessage();
  const { wifiPassword, setwifiPassword } = useMessage();
  const { fourG_apn, setfourG_apn } = useMessage();
  const { fourG_user, setfourG_user } = useMessage();
  const { fourG_password, setfourG_password } = useMessage();
  const { fourG_pin, setfourG_pin } = useMessage();


  const Tick = () => {
    return <img className="tick" src="/assets/img/tik.png" alt="" />
};

  const handleAddDiv = (text:string) => {
    const newDiv = (
      <div className="text3" key={divs.length}>
        {text}
      </div>
    );
    setDivs([...divs, newDiv]); 
  };

  const connectWebSocket = () => {
    console.log("Web", isConnecting)
    // if (isConnecting) return;

    setIsConnecting(true);


    const newSocket = new W3CWebSocket('ws://' + window.location.hostname + ':4000');

    newSocket.onopen = () => {
      console.log("WebSocket bağlantısı kuruldu.");
      navigate("");
      setSocket(newSocket);
      setIsConnecting(false);
    };

    newSocket.onmessage = (message) => {
      const jsonData = JSON.parse(message.data.toString());
      console.log(jsonData);
      switch (jsonData.Command) {
        case "USBList":
          setUSBList(jsonData.Data)
          break
        case "USBControl":
          if (jsonData.Data){
            handleAddDiv("Test Cihazına bağlandı.")
          }
          else{
            handleAddDiv("Test Cihazına bağlanamadı!")
          }
          break
        case "Config":
          if (jsonData.Data){

          }

          
          break


        // case "NetworkPriority":
        //   setNetworkPriority(message.data.toString());
        //   break

      }

    };

    newSocket.onerror = (error) => {
      console.error("WebSocket hatası:", error);
    };

    newSocket.onclose = () => {
      console.log("WebSocket bağlantısı kapatıldı.");
      setSocket(null);
      setIsConnecting(false);

      setTimeout(() => {
        connectWebSocket();
      }, 1000);
    };
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log("connectWebSocket...")
        connectWebSocket();
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();

    return () => {
      if (socket) {
        console.log("WebSocket bağlantısı kapatıldı.");
        socket.close();
      }
    };
  }, []);

  return <div></div>;
};

export default WebSocketComponent;
